#header {
    overflow:hidden;
    height: 100%;
    border: #00007e solid 2px;
    border-radius: 10px;
}

#footer-size {
    overflow:hidden;
    height: 100%;
    border: #00007e solid 1px;
    border-radius: 10px;
}

span {
    color:#00007e;
}

.heading-label {
    overflow:hidden;
    height: 100%;
    border: #999 solid 1px;
    border-radius: 10px;
    background-color: #00007e !important;
    font-family: 'Arial Bold';
    font-size: 13pt;
}

.heading-label div {
    padding: 8px;
    display: inline-block;
}


#header table td {
     vertical-align: top !important; 
     padding: 0px !important; 
}

.toc table td {
    padding: 0px !important;
    text-align:justify;
}

.form div {
    font-family: 'MS Sans-serif';
    font-size: 9.2pt;
    margin-top:0.4em;
}

.row1 div {
    position: relative;
    top: -10pt;
}

.ms-serif-8 {
    font-family: 'MS Sans-serif';
    font-size: 7pt;
    line-height: 1.1em !important;
    /*letter-spacing: -0.1em !important;*/
    color:#00007e;
}

.dashed-line {
    border-bottom: 1px dashed #00007e; text-align:center;
}

.circle {
    position: absolute;
    color: #00007e;
    box-shadow: 0px 0px 0px 3px #dddddd inset !important;
    width: 22px;
    height: 22px;
    border-radius: 50%;
    line-height: 21px;
    font-size: 11px;
    text-align: center;
    margin: 0 2px 0 2px;
}

.dot {
    position: absolute;
    width: 9px;
    height: 9px;
    border-radius: 50%;
    text-align: center;
    background-color:#00007e;
    margin: 3px 4px 0 4px;
}
